from httpx import Response
from dataclasses import fields, is_dataclass
from steam_trader.web import *
from dotenv import load_dotenv
load_dotenv()

class IndependentTests(unittest.TestCase):

    def assertion(self, result, expected_result):
        for result_field, expected_field in zip(fields(result), fields(expected_result)):
            if is_dataclass(result_field):
                self.assertion(result_field, expected_field)
            else:
                self.assertEqual(expected_result.__getattribute__(expected_field.name), result.__getattribute__(result_field.name))

